declare leng int;
select cwxzsz,cwpdsz,cztcwsz into cw_xzsz,cw_pdsz,czt_cwsz from xsdyjl where sjno=sj_no and UserId=user_id;
if lx=1 then
/*错误选择题*/
 /*len(','的个数) 的计算*/ 

        WHILE locate(',',cw_xzsz) > 0 DO
                BEGIN
