if lx=2 then
/*错误判断题*/
 /*len(','的个数) 的计算

select cwpdsz into cw_pdsz from xsdyjl where sjno=sj_no and UserId=user_id;*/ 
         WHILE locate(',',cw_pdsz) > 0 DO
                BEGIN
