                        select substring_index(v_z3,'|',1) into v_s; 
                            
                    end if;
                else
                    select substring_index(czt_cwsz,',',-1) into v_z3;                    
                    select substring_index(v_z3,'|',1) into v_s;                    
                   
                end if;                

                                     set nono=1;
