-- ----------------------------
-- Procedure structure for `queryByRoleid`
-- ----------------------------
DROP PROCEDURE IF EXISTS `queryByRoleid`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `queryByRoleid`(IN `param` int)
BEGIN

    /*sql语句*/
	select
	/*要查询的列：actionname,functionname功能名，mname模块名，mclassname分类名*/ 
	functions.actionname,functionname,modules.mname,moduleclass.mclassname
	/*从rolefunction,functions,modules,moduleclass四个表中查询*/
	from rolefunction,functions,modules,moduleclass
	/*查询条件，多表之间的联系通过主外键*/
	where roleid=param        /*参数值赋给roleid*/
	and rolefunction.actionname=functions.actionname 
	
	and functions.mname=modules.mname 
	and modules.mclassname=moduleclass.mclassname 
	/*查询结果的每一列都按升序排列*/
	order by moduleclass.classolder asc,modules.molderid asc ,functions.forder asc;
	/*sql语句结束*/
 
END
;;
DELIMITER ;

