DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `searchczt`(`beginchapter` int(11),`endchapter` int(11),`cztts` int(11))
BEGIN
    /*查询有文件,而且每道小题都有答案的大题 题号，并存放在临时表tmp_cztth中    */  
    DROP TABLE IF EXISTS tmp_cztth;
    create TEMPORARY TABLE tmp_cztth select distinct cztywj.th from cztywj,cztda  where cztywj.th=dt_th and zbh>=beginchapter and zbh<=endchapter and cztywj.th not in (
            select dt_th from czt where sxh not in (
                   select sxh from cztda where cztda.dt_th=czt.dt_th)
     );                          
                            
/*从临时表tmp_cztth中取出一定题数的操作题答题题号，并根据它来得到操作题的其它属性    */ 
  if cztts=0 then    
     /*如何没有操作题题数限制，则全部取出*/
     select distinct cztd.th,puguangdu,datitime,czt.nyd,cztda.zsdbh,xtfz from czt,cztd,cztda where czt.dt_th=cztd.th 
     and cztd.th=cztda.dt_th and cztd.th=cztda.dt_th and czt.dt_th in(select * from tmp_cztth);  
  else   
          /*如何有操作题题数限制，则取出相应个数的操作题*/
     select distinct cztd.th,puguangdu,datitime,czt.nyd,cztda.zsdbh,xtfz from czt,cztd,cztda where czt.dt_th=cztd.th 
     and cztd.th=cztda.dt_th and cztd.th=cztda.dt_th and czt.dt_th in 
                       (select * from(select * from tmp_cztth order by rand() limit cztts)as a);
  end if;      
  
    

END
;;
DELIMITER ;

