-- ----------------------------
-- Procedure structure for `SJ_ST2`
-- ----------------------------
DROP PROCEDURE IF EXISTS `SJ_ST2`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `SJ_ST2`(IN `sj_no` int)
begin
     
/*选择变量*/
        DECLARE v_cztsz varchar(500);               
        DECLARE v_cztsz1x varchar(500);
        DECLARE v_cztsz1 varchar(500);     
        DECLARE v_cztsz1x2 varchar(500); 
        DECLARE v_cztsz1x3 varchar(500); 
        DECLARE v_cztsz1x4 varchar(500);            
        DECLARE v_z varchar(500);  
        DECLARE v_o varchar(500);   
        DECLARE no int;
        DECLARE len int;
        DECLARE len1 int;
        DECLARE i int;   
        DECLARE j int;
        DECLARE v_zd varchar(500);
        DECLARE len1x1 int;        
        DECLARE len1x int;  
 
        select cztsz  into v_cztsz from sjnr where sjno=sj_no;        
        set v_cztsz1=v_cztsz;
        set len=0;
        /*len(','的个数) 的计算*/
         WHILE locate(',',v_cztsz1) > 0 DO
                BEGIN
                    SET len1 = LENGTH(v_cztsz1);
                    SET i = locate(',', v_cztsz1);            
                    SET len=len+1;
                    SET v_cztsz1 = RIGHT(v_cztsz1, len1 - i);
                END;                
         END WHILE; 
         set no=1;         
         set v_cztsz1x3="";
        while no<=len+1 do
           begin
                if len>=no then
                    if no=1 then 
                       select substring_index(v_cztsz,',',no) into v_z;                         
                    else
                        select substring_index(v_cztsz,',',no) into v_o;
                        select substring_index(v_o,',',-1) into v_z;                            
                    end if;
                else
                    select substring_index(v_cztsz,',',-1) into v_z;                                        
                end if;   

                    set v_cztsz1x=v_z;                                    
                     if locate('|',v_cztsz1x) > 0 then
                            BEGIN
                                SET len1x = LENGTH(v_cztsz1x);
                                SET j = locate('|', v_cztsz1x);                                            
                                SET v_cztsz1x2=RIGHT(v_cztsz1x, len1x - j);                                
                                select substring_index(v_cztsz1x,'|',1) into v_zd;  
                                set v_cztsz1x3=concat(v_cztsz1x3,",",v_zd); /*把大题题号拼接成一个字符串*/                                                 
                            END;                
                     END if;                              
                set no=no+1;       
           end;   
        end while;   
     SET len1x1 = LENGTH(v_cztsz1x3); 
     set v_cztsz1x4= RIGHT(v_cztsz1x3, len1x1 - 1);   
     select sxh,cztd.tg,th,czt.tg from czt,cztd where th=dt_th and  instr(concat(',',v_cztsz1x4,','),concat(',',th,','));  
end
;;
DELIMITER ;

