INSERT INTO `schoollist` VALUES ('114', '西安电子科技大学', '西安电子科技大学是国家“211工程”立项建设的重点高校之一，是全国55所设有研究生院的高校之一，37所示范性软件学院的高校之一，也是全国20所获批设立集成电路人才培养基地的高校之一。');
INSERT INTO `schoollist` VALUES ('115', '西安交通大学', '西安交通大学是首批进入国家“211工程”和“985工程”重点建设高校，1999年被国家确定为以建设世界知名高水平大学为目标的学校。');
