INSERT INTO `zsd` VALUES ('101002', '1', '101', '数据的概念', '数据：是由一组物理或符号组成，含有某种或某些信息的序列。', '数据', '0', '0');
INSERT INTO `zsd` VALUES ('101003', '1', '101', '信息和数据的关系', '信息和数据是两个相互联系、相互依存、又相互区别的概念。数据是信息的表示形式，信息是数据所表达的含义。数据是具体的物理形式，信息是抽象出来的逻辑意义。', '       ', '0', '0');
INSERT INTO `zsd` VALUES ('101004', '1', '101', '知识的概念', '知识是已知的、系统化的信息。知识都是信息，信息不全是知识。', '', '0', '0');
INSERT INTO `zsd` VALUES ('101005', '1', '101', '情报的概念', '情报是对用户有用的、经过传递到达用户的知识。情报是知识的一部分，未传递给用户的知识就不是情报。', '', '0', '0');
INSERT INTO `zsd` VALUES ('101006', '1', '101', '消息的概念', '消息是指包含某种内容的音讯。消息是信息的反映形式，信息是消息的实质内容。信息不同于消息，消息只是信息的外壳，信息则是消息的内核。', ' ', '0', '0');
INSERT INTO `zsd` VALUES ('101007', '1', '101', '信息的特征', '社会性、传载性、不灭性、共享性、时效性、能动性', '', '0', '0');
INSERT INTO `zsd` VALUES ('101008', '1', '101', '信息的分类', '', '', '0', '0');
INSERT INTO `zsd` VALUES ('101009', '1', '101', '信息的作用', '认知、管理、控制、交流、娱乐', '', '0', '0');
INSERT INTO `zsd` VALUES ('101010', '1', '101', '信息技术的概念', '信息技术就是能够提高或扩展人类信息能力的方法和手段的总称。这些方法和手段是指完成信息产生、获取、检索、识别、变换、处理、控制、分析、显示及利用的技术。', '', '1', '0');
INSERT INTO `zsd` VALUES ('101011', '1', '101', '信息技术的发展与特征', '以古代、近代和现代信息技术的特征人工、电信、网络为主要特征。', '', '0', '0');
INSERT INTO `zsd` VALUES ('101012', '1', '101', '信息技术的层次', '主体、应用、外围层', '', '0', '0');
INSERT INTO `zsd` VALUES ('101013', '1', '101', '信息技术的特点', '数字化、网络化、高速化、智能化、个人化', '', '0', '0');
INSERT INTO `zsd` VALUES ('101014', '1', '101', '信息技术的功能', '辅助、开发、协同、增效、先导', '', '0', '0');
INSERT INTO `zsd` VALUES ('101015', '1', '101', '信息技术的影响', '信息的负面效果：信息泛滥、信息污染、信息病毒、信息犯罪、信息渗透', '', '0', '0');
INSERT INTO `zsd` VALUES ('101016', '1', '101', '信息化', '通过信息技术开发、信息产业的发展、信息人才的配置，最大限度地利用信息资源以满足全社会的信息需求，从而加速社会各个领域的共同发展以推进到信息社会的过', null, '1', '0');
INSERT INTO `zsd` VALUES ('101017', '1', '101', '信息化社会的特征', '信息成为重要的战略资源、信息产业成为最大的产业、信息网络成为社会的基础设施', null, '0', '0');
INSERT INTO `zsd` VALUES ('101018', '1', '101', '社会信息化', '社会的信息化指社会系统的信息化，工厂自动化、办公自动化、家庭自动化；', null, '0', '0');
INSERT INTO `zsd` VALUES ('101019', '1', '101', '信息产业   ', null, null, '0', '0');
INSERT INTO `zsd` VALUES ('101020', '1', '101', '信息经济', '', '', '0', '0');
INSERT INTO `zsd` VALUES ('101021', '1', '101', '信息网络', null, null, '0', '0');
INSERT INTO `zsd` VALUES ('102001', '1', '102', '数制的种类', '二进制、十进制、八进制、十六进制', null, '0', '0');
INSERT INTO `zsd` VALUES ('102002', '1', '102', '数制', null, null, '1', '0');
INSERT INTO `zsd` VALUES ('102003', '1', '102', '数码', '在一种计数制中，只能使用一组固定的数字符号来表示数目的大小，这些数字符号被称为该计数制的数码。', null, '1', '0');
INSERT INTO `zsd` VALUES ('102004', '1', '102', '基数', '每种数制中数码的个数称为该数值的基数。', '', '1', '0');
INSERT INTO `zsd` VALUES ('102005', '1', '102', '进制', '计数制中位置制记数法规则“逢N进一”如：十进制中“逢十进一”，二进制中“逢二进一”，八进制中“逢八进一”，十六进制中“逢十六进一”', null, '1', '0');
INSERT INTO `zsd` VALUES ('102006', '1', '102', '位权', '在位置制记数法中，数码所处的位置不同，代表的数值大小也不同。对每一位数位赋予的值叫做位权，简称权。位权的值为基数的若干次幂。某一位数码代表的数值的大小是该位的数码与位权的乘积。', null, '1', '0');
INSERT INTO `zsd` VALUES ('102007', '1', '102', '进制的转换', '二进制、八进制、十六进制转换十进制采用按权展开再相加的方法。十进制转换成N进制采用除N取余的方法。', null, '1', '0');
INSERT INTO `zsd` VALUES ('102008', '1', '102', '二进制常用单位', null, '位；字节；字；千字节KB；兆字节MB；千兆字节GB', '1', '1');
INSERT INTO `zsd` VALUES ('102009', '1', '102', '字符编码', '将字符变为指定的二进制符号成为编码。在计算机内部要为每个字符指定一个确定的编码，作为识别与使用这些字符的依据。', '字符编码', '0', '0');
INSERT INTO `zsd` VALUES ('102010', '1', '102', 'ASCII码', 'ASCII码即美国标准信息交换码，包括32个通用控制符、10个十进制数码、52个英文大小写字母和32个专用符号，共128个元素，用一个字节即8位二进制数进行编码，规定其最高位总是0', 'ASCII码；美国标准信息交换码', '1', '0');
INSERT INTO `zsd` VALUES ('102011', '1', '102', 'BCD码', 'BCD码又称“二·十进制编码”，专门解决用二进制数表示十进制数的问题。二·十进制编码编码的方法很多，有8421码、2421码、5211码、余3码、右移码。', 'BCD码；二·十进制编码；8421码；2421码；5211码；余3码；右移码', '0', '0');
INSERT INTO `zsd` VALUES ('102012', '1', '102', '8421码', '用四位二进制数表示一位十进制数，自左至右每一位对应的位权势8、4、2、1。', null, '0', '0');
INSERT INTO `zsd` VALUES ('102013', '1', '102', '汉字编码', '汉字也是一种字符，常用汉字有3000～5000个，比西文字符量大且复杂，显然无法用一个字节得编码来区分，汉字用两个字节进行编码。', null, '0', '0');
INSERT INTO `zsd` VALUES ('102014', '1', '102', '国标码', '1981年我国公布的《通用汉字字符集及其交换码标准》GB2312-80，共收集了7445个图形字符，其中汉字字符6763个，并分为两极，即常用的一级汉字3755个（按汉语拼音顺序）和次常用汉字3008个（按偏旁部首排序），其他图形符号682个。国标码需占用2个字节，每个字节占用7位信息，最高位补0。', null, '0', '0');
INSERT INTO `zsd` VALUES ('102015', '1', '102', '汉字内码', '汉字内码是汉字在计算机内部存储、处理和传输用的信息代码，要求与ASCII码兼容但又不能相同，以便实现汉字和西文的并存兼容。将国标码两个字节的最高位置“1”作为汉字的内码。', '汉字内码', '0', '0');
INSERT INTO `zsd` VALUES ('102016', '1', '102', '汉字输入码', '汉字输入码又称为外码，是指从键盘输入汉字时采用的编码。主要有1、数字编码2、拼音码3、形码4、音形码', '汉字输入码；外码；数字编码；拼音码；形码；音形码', '0', '0');
INSERT INTO `zsd` VALUES ('102017', '1', '102', '数字编码', '用一串数字代表一个汉字，最常用的是国标区位码，把GB2312-8全部字符集分为94区，其中1～15区是字母、数字和图形符号，16～55区是一级汉字区，56～87区是二级汉字和偏旁部首区，每个区分为94位，编码也是从01～94。这样每个字符便有一个区码和一个位码。将区码置前、位码置后，组合在一起就是区位码。区位码和国标码一一对应。区位码是十进制表示的国标码，国标码是十六进制表示的区位码。', '区位码', '0', '0');
INSERT INTO `zsd` VALUES ('102018', '1', '102', '拼音码', '拼音码是以汉语拼音为基础的输入方法。', '拼音码；微软拼音码；智能ABC', '0', '0');
INSERT INTO `zsd` VALUES ('102019', '1', '102', '形码', '形码是根据汉字形状确定的编码。', '形码；五笔字型码；表形码', '0', '0');
INSERT INTO `zsd` VALUES ('102020', '1', '102', '音形码', '音形码根据汉字的读音和字形进行编码。', null, '0', '0');
INSERT INTO `zsd` VALUES ('102021', '1', '102', '汉字字形码', '汉字字形码用在输出时产生汉字的字形，通常采用点阵形式，故汉字字形码就是确定一个汉字字形点阵的位置。全点阵字形中的每一点用一个二进制位来表示，随着字形点阵的不同，所需要的二进制位数也不同。24×24的字形点阵，每字需要72字节；32×32的字形点阵，每字需要128字节。', '汉字字形码；汉字字形点阵', '0', '0');
INSERT INTO `zsd` VALUES ('102022', '1', '102', '数的编码', null, null, '0', '0');
