INSERT INTO `zsd` VALUES ('104019', '1', '104', '计算机病毒预防', '要让不明病毒情况的移动存储设备到自己的机上使用；对于交换的软件和数据文件，要先检测，确认无病毒时方可使用；对执行重要工作的机器，要专机专用，专盘专用；不要轻易打开或下载来历不明的文件或邮件；对重要文件，要经常进行备份，以便当系统遭到破坏时，能及时得以恢复。', null, '1', '0');
INSERT INTO `zsd` VALUES ('201001', '2', '201', '计算机的发展史', '自从世界上第一台电子计算机ENIAC（Electronic Numerical Integrator and Calculator）于1946年2月在美国宾夕法尼亚大学诞生，计算机技术迅速发展，在这50余年发展经历了四次重大技术革命，分为电子管、晶体管、中小规模集成电路、大规模和超大规模集成电路。通常称为四代。', 'ENIAC；美国宾夕法尼亚大学；电子管；晶体管；中小规模集成电路；大规模超大规模集成电路', '1', '0');
INSERT INTO `zsd` VALUES ('201002', '2', '201', '计算机发展趋势', '计算机总的发展趋势以下6个方面：1、微型化2、巨型化3、网络化4、智能化5、多媒体技术6、非冯﹒诺依曼型计算机', '微型化；巨型化；网络化；智能化；多媒体技术；非冯﹒诺依曼型计算机', '1', '0');
INSERT INTO `zsd` VALUES ('201003', '2', '201', '计算机分类', '1989年“电气与电子工程师协会”（IEEE）提出一种分类方法，将计算机分为六种，个人计算机、工作站、小型计算机、主机、小巨型计算机、巨型计算机。', '电气与电子工程师协会；个人计算机；工作站；小型计算机；主机；小巨型计算机；巨型计算机', '1', '0');
INSERT INTO `zsd` VALUES ('201003', '2', '203', '计算机的发展史', '自从世界上第一台电子计算机ENIAC（Electronic Numerical Integrator and Calculator）于1946年2月在美国宾夕法尼亚大学诞生，计算机技术迅速发展，在这50余年发展经历了四次重大技术革命，分为电子管、晶体管、中小规模集成电路、大规模和超大规模集成电路。通常称为四代。', 'ENIAC；美国宾夕法尼亚大学；电子管；晶体管；中小规模集成电路；大规模超大规模集成电路', '1', '0');
INSERT INTO `zsd` VALUES ('201004', '2', '201', '计算机特点', '计算机特点可概括为四个方面：1、运算速度快，2、计算精度高，3、具有超强的“记忆”和逻辑判断功能，4、能自动运行且支持人机交互', '运算速度；计算精度；记忆；逻辑判断功能；能自动运行；支持人机交互', '0', '0');
INSERT INTO `zsd` VALUES ('201005', '2', '201', '计算机应用', '随着超大规模集成电路的出现和计算机网络技术的迅速发展，微型计算机不断普及，信息资源日益丰富，使得计算机的应用渗透到社会各个领域，1、科学计算（数值计算）2、信息处理（数据处理）3、过程控制4、计算机辅助工程5、人工智能6、网络应用', '科学计算（数值计算）；信息处理（数据处理）；过程控制；计算机辅助工程；人工智能；网络应用', '0', '0');
INSERT INTO `zsd` VALUES ('202001', '2', '202', '计算机系统组成', '冯﹒诺依曼型计算机系统由硬件系统和软件系统两大部分组成。', '冯﹒诺依曼', '1', '0');
INSERT INTO `zsd` VALUES ('202002', '2', '202', '冯﹒诺依曼型计算机', '冯﹒诺依曼型计算机包含三个要点：1、采用二进制数的形式表示数据和指令；2、将指令和数据同时存放在存储器中；3、由控制器、运算器、存储器、输入设备、输出设备五大部分组成的计算机。', '冯﹒诺依曼；“程序存储和控制”原理', '1', '1');
INSERT INTO `zsd` VALUES ('202003', '2', '202', '非冯﹒诺依曼型计算机', '冯﹒诺依曼型计算机的“程序存储和控制”原理表现在“集中顺序控制”方面的串行机制，却成为进一步提高计算机性能的瓶颈，而提高计算机性能的方向之一是并行处理。因此，许多非冯﹒诺依曼型计算机理论不断出现，如“神经网络计算机”、“DNA计算机”、“光子计算机”', '非冯﹒诺依曼；神经网络计算机；光子计算机；DNA计算机', '0', '0');
INSERT INTO `zsd` VALUES ('202004', '2', '202', '计算机硬件系统', '计算机硬件系统是指构成计算机系统的物理实体或物理装置，它们是计算机进行工作的实体。', '计算机硬件系统；运算器；控制器；中央处理器；存储器；输入设备；输出设备', '1', '1');
INSERT INTO `zsd` VALUES ('202005', '2', '202', '计算机主要性能指标', '1、字长2、运算速度3、时钟频率/主频4、内存容量', '字长；运算速度；时钟频率/主频；内存容量', '0', '0');
INSERT INTO `zsd` VALUES ('202006', '2', '202', '计算机软件系统', '软件是指在硬件设备上运行的各种程序、数据及有关的文档资料。程序是用于指挥计算机执行各种动作已完成指定任务的指令集合。', '计算机软件系统；系统软件；操作系统；工具软件；程序设计语言；语言处理程序；应用软件', '0', '0');
INSERT INTO `zsd` VALUES ('202007', '2', '202', '计算机工作原理', '计算机工作原理即将程序和数据存储在内存中，在控制器的控制下逐条取出指令，分析和执行指令，完成相应的操作。', '计算机工作原理；取指令；分析指令；执行指令', '0', '0');
INSERT INTO `zsd` VALUES ('202008', '2', '202', '微型计算机系统的基本组成', '微型计算机（简称微机）系统由硬件系统和软件系统两大部分组成。', '微型计算机系统；微机；微型计算机；硬件系统；软件系统', '1', '0');
