INSERT INTO `zsd` VALUES ('203003', '2', '203', '媒体分类', '媒体是信息标识和传输的载体。媒体在计算机领域分为以下5类：1、感觉媒体2、表示媒体3、表现媒体4、存储媒体5、传输媒体', '感觉媒体；表示媒体；表现媒体；存储媒体；传输媒体', '1', '0');
INSERT INTO `zsd` VALUES ('203004', '2', '203', '媒体元素', '媒体元素是指多媒体应用中可以展示给用户的媒体组成，目前主要包括文本、超文本、图形、图像、声音、动画和视频等。', '文本；超文本；图形；图像；声音；动画和视频', '0', '0');
